"use strict";

/* =============================================================================
 * 
 * Enums
 *  
 * ========================================================================== */

var Enums = module.exports;

/* -------------------------------------------------------------------
 * Enums << Keep in alphabetical order >>
 * ---------------------------------------------------------------- */

Enums.PostTypes = {
	FEATURE: 1,
	NOTE: 2
};

Enums.UserTypes = {
	UNREGISTERED: 0,
	SUSPENDED: 10,
	NORMAL: 20,
	CONTRIBUTOR: 30,
	EDITOR: 40,
	ADMIN: 50
};

/* -------------------------------------------------------------------
 * Private Methods << Keep in alphabetical order >>
 * ---------------------------------------------------------------- */

function enumify (en)
{
	var names = Object.getOwnPropertyNames(en);
	var n, v;
	for (var i = 0; i < names.length; i++)
	{
		n = names[i];
		v = en[n];
		Object.defineProperty(en, v, { value: n });
	}
	
	Object.freeze(en);
}

/* -------------------------------------------------------------------
 * Initialization
 * ---------------------------------------------------------------- */

for (var i in Enums)
	enumify(Enums[i]);

Object.freeze(Enums);
